mapper.select:
select id
from board_type
where name = #{boardTypeName};

select id
from category
where name = #{categoryName};

mapper.update:
update board
set state = 1
where id = #{boardId}

mapper.delete:
delete
from board_like
where board_id = #{boardId}
  and user_id = #{userId}

mapper.select:
select count(*)
from board
where category_id = #{categoryId}
  and type_id = #{boardTypeId}

select name
from category
where id = #{categoryId}

select id
from board_type
where id = #{boardTypeId}

select *
from board_like
where board_id = #{boardId}
  and user_id = #{userId}

select *
from board_report
where board_id = #{boardId}
  and user_id = #{userId}

select name
from report
where id = #{reportId}

select id
from board
where id = #{boardId}

mapper.update:
